import { Router } from 'express';
import { createCourseHandler, deleteCourseHandler, listCoursesHandler, updateCourseHandler, updateCourseStatusHandler } from '../controllers/courseController.js';
import { authenticate } from '../middleware/auth.js';
const router = Router();
router.use(authenticate());
router.get('/', listCoursesHandler);
router.post('/', createCourseHandler);
router.put('/:id', updateCourseHandler);
router.patch('/:id/status', updateCourseStatusHandler);
router.delete('/:id', deleteCourseHandler);
export default router;
